      } else {
        // Alert the user that they are not allowed to log in if they are not an admin
        alert("You are not allowed to log in. Only admins can access this page.");
      }
    }
  };

  return (
    <div
      style={{
        height: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <input
        style={{ padding: 10, marginBottom: 20 }}
        type="text"
        placeholder="username"
        onChange={(e) => setUsername(e.target.value)}
      />
      <input
        style={{ padding: 10, marginBottom: 20 }}
        type="password"
        placeholder="password"
        onChange={(e) => setPassword(e.target.value)}
      />
      <button onClick={handleClick} disabled={isFetching} style={{ padding: 10, width: 100 }}>
        Login
      </button>
      {error && <span style={{ color: "red", marginTop: 10 }}>Something went wrong!</span>}
    </div>
  );
};

export default Login;
