import { Link, useLocation } from "react-router-dom";
import "./product.css";
import Chart from "../../components/chart/Chart";
import { Publish } from "@material-ui/icons";
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useMemo, useState } from "react";
import { userRequest } from "../../requestMethods";
import { updateProductStart, updateProductSuccess, updateProductFailure } from "../../redux/productRedux"; // Import the update actions

export default function Product() {
  const location = useLocation();
  const productId = location.pathname.split("/")[2];
  const [pStats, setPStats] = useState([]);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState(0);
  const [image, setImage] = useState(""); // State for a single image
  const [countInStock, setCountInStock] = useState(true);
  const [category, setCategory] = useState("");
  const [size, setSize] = useState([]);
  const [color, setColor] = useState([]);

  const dispatch = useDispatch();

  const product = useSelector((state) =>
    state.product.products.find((product) => product._id === productId)
  );

  const MONTHS = useMemo(
    () => [
      "Jan", "Feb", "Mar", "Apr", "May", "Jun",
      "Jul", "Aug", "Sep", "Oct", "Nov", "Dec",
    ],
    []
  );

  useEffect(() => {
    const getStats = async () => {
      try {
        const res = await userRequest.get("orders/income?pid=" + productId);
        const list = res.data.sort((a, b) => {
          return a._id - b._id;
        });
        list.map((item) =>
          setPStats((prev) => [
            ...prev,
            { name: MONTHS[item._id - 1], Sales: item.total },
          ])
        );
      } catch (err) {
        console.log(err);
      }
    };
    getStats();
  }, [productId, MONTHS]);

  useEffect(() => {
    if (product) {
      setTitle(product.title);
      setDescription(product.description);
      setPrice(product.price);
      setImage(product.imageUrl); // Load existing image
      setCountInStock(product.countInStock);
      setCategory(product.category.join(", "));
      setSize(product.size);
      setColor(product.color);
    }
  }, [product]);

  const handleImageUpload = async (e) => {
    const file = e.target.files[0]; // Get the first file
    if (file) {
      const url = await uploadImageToFirebase(file); // Function to upload to Firebase and get the URL
      setImage(url); // Update state with the new image URL
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const updatedProduct = {
      title,
      description,
      price,
      image, // Include the single image URL
      category: category.split(", "), // Convert back to array
      size,
      color,
      countInStock,
    };

    dispatch(updateProductStart()); // Start the update process
    try {
      // Assuming you have an API endpoint to update the product
      await userRequest.put(`/products/${productId}`, updatedProduct);
      dispatch(updateProductSuccess({ id: productId, product: updatedProduct })); // Dispatch success action
    } catch (error) {
      dispatch(updateProductFailure()); // Dispatch failure action
      console.error("Failed to update product:", error);
    }
  };

  return (
    <div className="product">
      <div className="productTitleContainer">
        <h1 className="productTitle">Product</h1>
        <Link to="/newproduct">
          <button className="productAddButton">Create</button>
        </Link>
      </div>
      <div className="productTop">
        <div className="productTopLeft">
          <Chart data={pStats} dataKey="Sales" title="Sales Performance" />
        </div>
        <div className="productTopRight">
          <div className="productInfoTop">
            <img src={imageUrl} alt="" className="productInfoImg" />
            <span className="productName">{title}</span>
          </div>
          <div className="productInfoBottom">
            <div className="productInfoItem">
              <span className="productInfoKey">id:</span>
              <span className="productInfoValue">{product._id}</span>
            </div>
            <div className="productInfoItem">
              <span className="productInfoKey">sales:</span>
              <span className="productInfoValue">5123</span>
            </div>
            <div className="productInfoItem">
              <span className="productInfoKey">in stock:</span>
              <span className="productInfoValue">{countInStock ? "Yes" : "No"}</span>
            </div>
            <div className="productInfoItem">
              <span className="productInfoKey">category:</span>
              <span className="productInfoValue">{product.category.join(", ")}</span>
            </div>
            <div className="productInfoItem">
              <span className="productInfoKey">sizes:</span>
              <span className="productInfoValue">{product.size.join(", ")}</span>
            </div>
            <div className="productInfoItem">
              <span className="productInfoKey">colors:</span>
              <span className="productInfoValue">{product.color.join(", ")}</span>
            </div>
          </div>
        </div>
      </div>
      <div className="productBottom">
        <form className="productForm" onSubmit={handleSubmit}>
          <div className="productFormLeft">
            <label>Product Name</label>
            <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
            <label>Product Description</label>
            <input type="text" value={description} onChange={(e) => setDescription(e.target.value)} />
            <label>Price</label>
            <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} />
            <label>In Stock</label>
            <select value={countInStock} onChange={(e) => setCountInStock(e.target.value === "true")}>
              <option value="true">Yes</option>
              <option value="false">No</option>
            </select>
            <label>Category</label>
            <input type="text" value={category} onChange={(e) => setCategory(e.target.value)} />
            <label>Sizes</label>
            <input type="text" value={size.join(", ")} onChange={(e) => setSize(e.target.value.split(", "))} />
            <label>Colors</label>
            <input type="text" value={color.join(", ")} onChange={(e) => setColor(e.target.value.split(", "))} />
          </div>
          <div className="productFormRight">
            <div className="productUpload">
              <img src={image} alt="" className="productUploadImg" />
              <label htmlFor="file">
                <Publish />
              </label>
              <input type="file" id="file" onChange={handleImageUpload} style={{ display: "none" }} />
            </div>
            <button className="productButton" type="submit">Update</button>
          </div>
        </form>
      </div>
    </div>
  );
}
